#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="S:\aptos\node_modules\.pnpm\@aptos-labs+aptos-cli@1.0.2\node_modules\@aptos-labs\aptos-cli\dist\node_modules;S:\aptos\node_modules\.pnpm\@aptos-labs+aptos-cli@1.0.2\node_modules\@aptos-labs\aptos-cli\node_modules;S:\aptos\node_modules\.pnpm\@aptos-labs+aptos-cli@1.0.2\node_modules\@aptos-labs\node_modules;S:\aptos\node_modules\.pnpm\@aptos-labs+aptos-cli@1.0.2\node_modules;S:\aptos\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/s/aptos/node_modules/.pnpm/@aptos-labs+aptos-cli@1.0.2/node_modules/@aptos-labs/aptos-cli/dist/node_modules:/mnt/s/aptos/node_modules/.pnpm/@aptos-labs+aptos-cli@1.0.2/node_modules/@aptos-labs/aptos-cli/node_modules:/mnt/s/aptos/node_modules/.pnpm/@aptos-labs+aptos-cli@1.0.2/node_modules/@aptos-labs/node_modules:/mnt/s/aptos/node_modules/.pnpm/@aptos-labs+aptos-cli@1.0.2/node_modules:/mnt/s/aptos/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../dist/aptos.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../dist/aptos.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../dist/aptos.js" $args
  } else {
    & "node$exe"  "$basedir/../../dist/aptos.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
